    const [language, setLanguage] = useState("default");
    const [direction, setDirection] = useState("");

    //const languages = data["allLanguages"].split("-").map((element)=> element === "en" ? "default" : element)
    const languages = data["textSoftware"][0]["item"].map(element => element.lang)

    const worl = new Map(data["textSoftware"].map(
        (value) => {
            const textId = value["text_id"];
            const choices = new Map(value["item"].map((obj) => [obj.lang, obj.text]));
            return [textId, choices];
        }
    ))

    console.log(languages)


    const selectLanguage = () => {
        const randomLanguage = languages[Math.floor(Math.random() * languages.length)];
        setLanguage(randomLanguage)
    };

    useEffect(() => {
        selectLanguage();
    }, []);

    return (
    <div>
        {language}:_{textSoftware.get("nameOfLanguage").get(language)}_{textSoftware.get("direction").get(language)}
        </div>)
}

export default MultilanguageDirection;
