import { useEffect, useState } from "react";
import data from "./data/languages_all.json";


function MultilanguageDirection() {
    const [language, setLanguage] = useState("default");
    const [direction, setDirection] = useState("");
    const [buttonText, setButtonText] = useState("");

    //const languages = data["allLanguages"].split("-").map((element)=> element === "en" ? "default" : element)
    const languages = data["textSoftware"].filter(element => element["text_id"]==="direction")[0]["item"].map(element => element.lang)

    const wordMap = new Map(data["textSoftware"].map(
        (value) => {
            const textId = value["text_id"];
            const choices = new Map(value["item"].map((obj) => [obj.lang, obj.text]));
            return [textId, choices];
        }
    ));


    const selectLanguage = () => {
        const randomLanguage = languages[Math.floor(Math.random() * languages.length)];
        setLanguage(randomLanguage)
    };

    useEffect(() => {
        let buttonText = language+"/"+wordMap.get("nameOfLanguage").get(language)
        setButtonText(buttonText);
        setDirection(wordMap.get("direction").get(language))
    }, [language]);

    function handleClick() {
        selectLanguage()
    };

    return (
        <div>
             <button onClick={handleClick}>{buttonText}</button>
             <span>{direction}</span>
        </div>
    );
}

export default MultilanguageDirection;
